
public class ExampleDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 1;
		short c = 2;
		int b = 10;
		long d = 584793l;
		float f = 38.4923f;
		double e = 45.3453;
		boolean w = true;
		char p = 'j';

		System.out.println("byte value is: " + a);
		System.out.println("short value is: " + c);
		System.out.println("int value is: " + b);
		System.out.println("long value is: " + d);
		System.out.println("float value is: " + f);
		System.out.println("double value is: " + e);
		System.out.println("boolean value is: " + w);
		System.out.println("char value is: " + p);
	}

}
